mapper.update:
update s_user
set account = #{account,jdbcType=VARCHAR},
  password = #{password,jdbcType=VARCHAR},
  login_time = #{loginTime,jdbcType=TIMESTAMP}
where user_id = #{userId,jdbcType=INTEGER}

mapper.select:
select user_id, account, password, login_time
from s_user
where user_id = #{userId,jdbcType=INTEGER}
